ssh.exec:
declare almsg="Package '§[package.name]§.§[package.arch]§' is already installed" declare okmsg="Package '§[package.name]§.§[package.arch]§' successfully installed" declare errmsg="Fail to install package '§[package.name]§.§[package.arch]§'"
declare testcmd="rpm -q §[package.name]§.§[package.arch]§" declare docmd="yum install §[package.repo]§ -y §[package.name]§.§[package.arch]§"
declare retryallowedmsg="(\ error communicating with server\ |no address associated with hostname\ |temporary failure in name resolution\ |unable to connect to the host and port specified\ |connection timed out\ |network is unreachable\ |name or service not known\ |could not resolve host\ |error downloading packages\ )"
eval ${testcmd} && { echo "${almsg}. Nothing to do." exit 0 }
declare retry=3 ret=0 out="$(mktemp --suffix=.out)" err="$(mktemp --suffix=.err)"
while [ ${retry} -ge 0 ]; do eval ${docmd} 1> >(tee "${out}") 2> >(tee "${err}" >&2) [ $? = 0 ] && break cat "${out}" "${err}" | grep -Ei "${retryallowedmsg}" 1>/dev/null || { retry=-1; break; } echo "---> ${errmsg}. ${retry} try left." >&2 retry=$((retry-1)) [ $retry = -1 ] && break sleep $(((3-retry)*4)) done
[ ${retry} = -1 ] && { echo "${errmsg}. Exiting with error code 1." >&2 ret=1 } || { echo "${okmsg}." }
rm -f "${out}" "${err}" exit ${ret}


rpm -q §[package.name]§.§[package.arch]§ || {
  echo "Package '§[package.name]§.§[package.arch]§' is already removed. Nothing to do."
  exit 0
}
yum remove -y §[package.name]§.§[package.arch]§ || {
  res=$?
  echo "Fail to remove package '§[package.name]§.§[package.arch]§'. Check log for details. Exiting with error code $res." >&2
  exit $res
}
echo "Package '§[package.name]§.§[package.arch]§' successfully removed."

exit 0
